example.programlisting:
"command.event": [Event] Category=command
[Relay] Server=myothercomputer.local
"command-success.event"; [Event] Extends=command.event Name=success
[Notification] Title=${command} succeeded Message=${time}
[Sound] File=file://command-success.ogg
[Libnotify] Title=${command} succeeded Message=${time}
[Libcanberra] Name=command-success
"command-failure.event"; [Event] Extends=command.event Name=failure
[Notification] Title=${command} failed Message=${time}
[NotificationBubble] Colour=#ff0000
[Sound] File=file://command-failure.ogg
[Libnotify] Title=${command} failed Message=${time}
[Libcanberra] Name=command-failure

Zshell zshrc: precmd() { eventc command $([ $? -eq 0 ] && echo success || echo failure) -d command -c "!!" }
Bash bashrc: precmd() { command=$(history -1) [[ -z "${command} ]] && return eventc command $([ $? -eq 0 ] && echo success || echo failure) -d command -c "${command}" } # This command is called anytime the prompt is displayed # so this may not work perfectly as expected PROMPT_COMMAND=precmd
Fish config: function notify_on_command_duration --on-variable CMD_DURATION eventc command $([ $? -eq 0 ] && echo success || echo failure) -d command -c "$_" -d time -c "$CMD_DURATION" end
