example.programlisting:
"eventd.conf": [Queue utility] Anchor=top-left Margin=0 Spacing=0 Limit=1
"notification-utility.event": [Event notification mouse-tool] IfDataMatches=client-name,==,"mouse-tool"; Actions=notification-utility;
[Event notification volume-tool] IfDataMatches=client-name,==,"volume-tool"; Actions=notification-utility;
"notification-utility.action": [Action] Name=notification-utility
[Notification] Text=${title} ${message}
[NotificationBubble] Queue=utility Padding=2 Radius=0 BorderBlur=0
[NotificationImage] Height=25 Anchor=Center
[NotificationIcon] Placement=Foreground Height=25

"eventd.conf": [Relay] Relays=command;
[Relay command] Forwards=command; Server=myothercomputer.local
"command.event": [Event command success] Actions=command-success;
[Event command failure] Actions=command-failure;
"command-success.action": [Action] Name=command-success
[Notification] Title=${command} succeeded Message=${time}
[Sound] File=file://command-success.ogg
[Libnotify] Title=${command} succeeded Message=${time}
[Libcanberra] Name=command-success
"command-failure.action": [Action] Name=command-failure
[Notification] Title=${command} failed Message=${time}
[NotificationBubble] Colour=#ff0000
[Sound] File=file://command-failure.ogg
[Libnotify] Title=${command} failed Message=${time}
[Libcanberra] Name=command-failure

Zshell zshrc: precmd() { local s=$? c=( $(fc -l -D -1 ) ) eventc command $([[ ${s} == 0 ]] && echo success || echo failure) -d command="${c[3,-1]}" -d time="${c[2]}" }
Bash bashrc: precmd() { command=$(history -1) [[ -z "${command} ]] && return eventc command $([ $? -eq 0 ] && echo success || echo failure) -d command="${command}" } # This command is called anytime the prompt is displayed # so this may not work perfectly as expected PROMPT_COMMAND=precmd
Fish config: function notify_on_command_duration --on-variable CMD_DURATION eventc command $([ $? -eq 0 ] && echo success || echo failure) -d command="$_" -d time="$CMD_DURATION" end

"eventd.conf": [Relay] Relays=one;
[Relay one] Server=your.distant.computer Forwards=foo;bar;baz;
"foo.event": [Event foo]
[Event bar]
[Event baz]
